import React, {useState} from "react";
import Card from 'react-bootstrap/Card';


function Artist({pName, pCity, pImage_link, pGenres}) {

  const [name, setName] = useState("");
  const [city, setCity] = useState("");
  const [image_link, setImage_link] = useState("");
  const [genres, setGenres] = useState("");

  function handleSubmit(event) {
    const formData = {
      name: name,
      city: city,
      image_link: image_link,
      genres: genres
    };
    console.log(formData);
    event.preventDefault();
    fetch('http://localhost:9292/artists', {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    });}

  return (
    <div class="col">
      <div class="col-sm-10">
        <div class="card" style={{ width: '25rem' }}>
          <div class="card-body">
            <h5 class="card-name">{pName}</h5>
            <h5 class="card-city">{pCity}</h5>
            <Card.Img variant="top" src= {pImage_link}/>
            <h5 class="card-genres">{pGenres}</h5>
          </div>
        </div>
      </div>
<form onSubmit={handleSubmit}  class="mb-3" className="new-artist-form"> 
     <input value={name}
  onChange={(e) => setName(e.target.value)} placeholder="NAME" />
     <input value={city}
  onChange={(e) => setCity(e.target.value)} placeholder="CITY" />
     <input value={image_link}
  onChange={(e) => setImage_link(e.target.value)} placeholder="IMAGE_LINK" />
     <input value={genres}
  onChange={(e) => setGenres(e.target.value)} placeholder="Genres" />
      <input type="submit" value="POST ARTISTS" />
</form>
      </div>
);

}

export default Artist;
