import React, {useState, useEffect} from "react";
import { useNavigate, useParams } from 'react-router-dom';

function ArtistEditForm() {
  const [name, setName] = useState("");
  const [city, setCity] = useState("");
  const [image_link, setImage_link] = useState("");
  const [genres, setGenres] = useState("");
  const [artist_data, setArtistData] = ({})


  function handleSubmit(event) {
    let { id } = useParams()
    useEffect(()=>{
        fetch(`http://localhost:9292/artists/${id}`)
        .then(response=>response.json())
        .then(data=>setArtistData(data))
    },[])
    console.log(artist_data)
    const formData = {
      name: name,
      city: city,
      image_link: image_link,
      genres: genres
    };
    console.log(formData);
    event.preventDefault();
    fetch('http://localhost:9292/artists', {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    });}


 return (
   <div class= "form" >
    <form onSubmit={handleSubmit}  class="newform" className="newform"> 
     <input value={name}
  onChange={(e) => setName(e.target.value)} placeholder="NAME" />
     <input value={city}
  onChange={(e) => setCity(e.target.value)} placeholder="CITY" />
     <input value={image_link}
  onChange={(e) => setImage_link(e.target.value)} placeholder="IMAGE_LINK" />
     <input value={genres}
  onChange={(e) => setGenres(e.target.value)} placeholder="Genres" />
      <input type="submit" value="POST ARTISTS" />
</form>
</div>
);
}

export default ArtistEditForm;
