import React  from "react";
import Card from 'react-bootstrap/Card';

const poemAPI = "http://localhost:9292/shows"

function Artist({poem, removePoem}) {
  const {name, city, image_link, genres} = poem
  function onDeleteClick(e) {
    e.preventDefault();
    fetch(`${poemAPI}/${poem.id}`, {
      method: "DELETE",
    });
    removePoem(poem);
  }

  return (
    <div class= "scard" >
    <div class="col">
      <div class="col-sm-10">
        <div class="card" style={{ width: '28rem' }}>
          <div class="card-body">
            <h5 class="card-name">ARTIST: {pName}</h5>
            <h5 class="card-city">CITY: {pCity}</h5>
            <Card.Img variant="top" src= {pImage_link} style={{height: 200 + 'px', width:400 + 'px'}}/>
            <h5 class="card-genres">GENRE: {pGenres}</h5>
            <div class="d-grid gap-2 d-md-flex justify-content-md-end">
           <button onclick ={onDeleteClick} class="btn btn-primary" type="button">DELETE</button>
           </div>
          </div>
        </div>
      </div>

      </div>
      </div>
);

}

export default Artist;
